import React,{useEffect,useState} from 'react'
import "./style.css"
import axios from "axios"


function Carusel() {
  const [data,setdata] = useState([])
  useEffect(() => {
   axios
   .get("http://myjson.dit.upm.es/api/bins/3m4h")
   .then((ress) => {
     setdata(ress.data)
     console.log(ress.data)
   })

  },[])
  return (
    <>
    <div><div class="carus swiffy-slider slider-item-show4 slider-nav-outside slider-nav-dark slider-nav-sm slider-nav-visible slider-nav-page slider-item-snapstart slider-nav-autoplay slider-nav-autopause slider-item-ratio slider-item-ratio-contain slider-item-ratio bg-white shadow-lg " data-slider-nav-autoplay-interval="2000">
    <div class="slider-container">
    {
      data.map((e) => {
        return(
          <img class="carus_img" src={e.profile_img_src} alt="salom"/>
        )
      })
    }

    </div>

    <button type="button" class="slider-nav" aria-label="Go left"></button>
    <button type="button" class="slider-nav slider-nav-next" aria-label="Go left"></button>

</div></div>    
    </>

  )
}

export default Carusel
